const BooksList = (props) => {
    let books=props.data
    let Remove=props.fn
    let RemoveAll=props.fn2
    return ( 
        <div style={{backgroundColor:"teal"}} className="bookslist">
            <h1>List of books:{books.length}</h1>
            {books.map((data)=>(
                <div style={{border:"2px solid black"}}>
                    <h1 style={{color:"red"}}>title:{data.title}</h1>
                    <h2>author:{data.author}</h2>
                    <h4>published in the year:{data.year}</h4>
                    <p>{data.pages} pages/ language:{data.language}</p>
                    <a href={data.link}>Read More</a>
                    <button onClick={()=>Remove(data.title)}>Remove</button>
                    <button onClick={RemoveAll}>Remove all</button>
                </div>
            ))

            }
        </div>
     );
}
 
export default BooksList;
