import { useState,useEffect } from "react";
const Users = () => {
    let [userData,setData]=useState([])
    useEffect(()=>{
        try{
            let fetchData = async () => {
                let response = await fetch("https://api.github.com/users");
                let data = await response.json();
                setData(data);
              };
              fetchData();
        }
        catch{
            console.log("failed to fetch,network issue");
        }
    })
    let Remove=(login)=>{
        setData(userData.filter(x=>x.login!==login))
        alert(`${login}has been removed `)
      }
    return ( 
        <div className="users">
            <h1>Github users page</h1>
            <p>Number of</p>
            { userData.map((data)=>(
               
                <div className="userList">
                <img width="150" src={data.avatar_url} alt="" /> 

                   <h1>Name:{data.login}</h1>
                   <button onClick={()=>Remove(data.login)}>Remove</button>
                </div>
            ))}
        </div>
     );
}
 
export default Users;
